package pl.ppyrczak.restaurant.service;

import org.assertj.core.api.Assertions;
import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.junit.runner.RunWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.MockitoJUnitRunner;
import org.springframework.boot.test.context.SpringBootTest;
import pl.ppyrczak.restaurant.enums.Meal;
import pl.ppyrczak.restaurant.model.Dish;
import pl.ppyrczak.restaurant.repository.DishRepository;

import java.math.BigDecimal;
import java.util.ArrayList;
import java.util.List;
import java.util.Optional;

import static org.assertj.core.api.Assertions.assertThat;
import static org.mockito.ArgumentMatchers.eq;
import static org.mockito.BDDMockito.given;
import static org.mockito.Mockito.*;
import static pl.ppyrczak.restaurant.enums.Cuisine.ITALIAN;
import static pl.ppyrczak.restaurant.enums.Meal.MEAT;

@RunWith(MockitoJUnitRunner.class)
@SpringBootTest
class DishServiceTest {
    @Mock
    private DishRepository dishRepository;
    @InjectMocks
    private DishService underTest;

    @BeforeEach
    void setUp() {
    }

    @AfterEach
    void tearDown() {
    }

    private Dish createDish() {
        Dish dish = new Dish(
                "data:image/jpeg;base64,",
                "pizza pepperoni",
                ITALIAN,
                Meal.MAIN,
                "Sauce, Cheese, pepperoni",
                20,
                BigDecimal.valueOf(30L),
                "Good pizza"
        );
        return dish;
    }

    @Test
    void should_add_dish() {
        Dish dish = createDish();
        given(dishRepository.save(dish)).willReturn(dish);

        underTest.addDish(dish);
        verify(dishRepository).save(eq(dish));
    }

    @Test
    void should_get_dishes() {
        Dish dish = createDish();
        Dish dish1 = new Dish();
        dish1.setName("Spaghetti Bolognese");
        dish1.setImageUrl("url");
        dish1.setCuisine(ITALIAN);
        dish1.setMeal(MEAT);
        dish1.setIngredients("Pasta, Grana Padano, beef, passata");
        dish1.setDishLimit(30);
        dish1.setPrice(BigDecimal.valueOf(14));
        dish1.setDescription("Tasty pasta");

        List<Dish> dishes = new ArrayList<>();
        dishes.add(dish);
        dishes.add(dish1);

        given(dishRepository.findAll()).willReturn(dishes);

        underTest.getDishes();
        verify(dishRepository).findAll();
    }

    @Test
    void should_get_dish() {
        Dish dish = createDish();
        given(dishRepository.findById(dish.getId())).willReturn(Optional.of(dish));
        underTest.getDish(dish.getId());
        verify(dishRepository).findById(dish.getId());

        Dish testDish = underTest.getDish(dish.getId());
        assertThat(testDish.getName()).isEqualTo("pizza pepperoni");
        assertThat(testDish.getIngredients()).isEqualTo("Sauce, Cheese, pepperoni");
    }

    @Test
    void should_edit_dish() {
        Dish dish = createDish();

        Dish dishToUpdate = new Dish();
        dishToUpdate.setName("Spaghetti Bolognese");
        dishToUpdate.setImageUrl("url");
        dishToUpdate.setCuisine(ITALIAN);
        dishToUpdate.setMeal(MEAT);
        dishToUpdate.setIngredients("Pasta, Grana Padano, beef, passata");
        dishToUpdate.setDishLimit(30);
        dishToUpdate.setPrice(BigDecimal.valueOf(14));
        dishToUpdate.setDescription("Tasty pasta");

        given(dishRepository.save(dish)).willReturn(dish);
        given(dishRepository.findById(dish.getId())).willReturn(Optional.of(dish));
        underTest.editDish(dish.getId(), dishToUpdate);
        verify(dishRepository).save(dish);

        assertThat(dish.getDescription()).isEqualTo(dishToUpdate.getDescription());
        assertThat(dish.getName()).isEqualTo(dishToUpdate.getName());
        assertThat(dish.getImageUrl()).isEqualTo(dishToUpdate.getImageUrl());
        assertThat(dish.getMeal()).isEqualTo(dishToUpdate.getMeal());
        assertThat(dish.getIngredients()).isEqualTo(dishToUpdate.getIngredients());
    }

    @Test
    void should_delete_dish() {
        Dish dish = createDish();
        when(dishRepository.findById(dish.getId())).thenReturn(Optional.of(dish));
        underTest.deleteDish(dish.getId());
        verify(dishRepository).deleteById(dish.getId());
    }
}
